INSERT INTO `wp_comments` (`comment_ID`, `comment_post_ID`, `comment_author`, `comment_author_email`, `comment_author_url`, `comment_author_IP`, `comment_date`, `comment_date_gmt`, `comment_content`, `comment_karma`, `comment_approved`, `comment_agent`, `comment_type`, `comment_parent`, `user_id`) VALUES
(1, 1, 'Một người bình luận WordPress', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2017-02-22 05:22:54', '2017-02-22 05:22:54', 'Xin chào, đây là một bình luận\nĐể bắt đầu với quản trị bình luận, chỉnh sửa hoặc xóa bình luận, vui lòng truy cập vào khu vực Bình luận trong trang quản trị.\nAvatar của người bình luận sử dụng <a href="https://gravatar.com">Gravatar</a>.', 0, '1', '', '', 0, 0),
(2, 61, 'WooCommerce', '', '', '', '2017-02-23 03:18:18', '2017-02-23 03:18:18', 'Thực hiện thanh toán khi giao hàng Trạng thái đơn hàng đã được chuyển từ Thanh toán chờ xử lý sang Đang xử lý.', 0, '1', 'WooCommerce', 'order_note', 0, 0),
(3, 61, 'WooCommerce', '', '', '', '2017-02-23 03:18:21', '2017-02-23 03:18:21', 'Số lượng sản phẩm MFD23 giảm từ 10 xuống 9.', 0, '1', 'WooCommerce', 'order_note', 0, 0);
